mapper.select:
SELECT
IFNULL(SUM(CASE
WHEN type IN

    #{t}

THEN amount ELSE 0 END), 0) AS totalIncome,

IFNULL(SUM(CASE
WHEN type IN

    #{t}

THEN ABS(amount) ELSE 0 END), 0) AS totalExpense,

IFNULL(SUM(CASE
WHEN create_time >= #{monthStart}
AND type IN

    #{t}

THEN amount ELSE 0 END), 0) AS monthIncome,

IFNULL(SUM(CASE
WHEN create_time >= #{monthStart}
AND type IN

    #{t}

THEN ABS(amount) ELSE 0 END), 0) AS monthExpense

FROM t_transaction_log
WHERE user_id = #{userId}
